BEGIN TRANSACTION;
CREATE TABLE "users" ("id" integer not null primary key autoincrement, "username" varchar not null, "password" varchar not null, "name" varchar not null, "email" varchar not null, "photo" varchar not null);
--INSERT INTO `users` VALUES(1,'alexbilbie','$2y$10$hZEDpwByBA05/ZTQaQozZe/inW.IcFhZhbpXOSyQbiOd04vWLujNG','Alex Bilbie','hello@alexbilbie.com','https://s.gravatar.com/avatar/14902eb1dac66b8458ebbb481d80f0a3');
--INSERT INTO `users` VALUES(2,'philsturgeon','$2y$10$473th0UCj3v3DukSFVOA4eIuKM5vBqE.rds8JxpXUn1HW9it1a7GW','Phil Sturgeon','email@philsturgeon.co.uk','https://s.gravatar.com/avatar/14df293d6c5cd6f05996dfc606a6a951');
CREATE TABLE "oauth_sessions" ("id" integer not null primary key autoincrement, "owner_type" varchar not null, "owner_id" varchar not null, "client_id" varchar not null, "client_redirect_uri" varchar null, foreign key("client_id") references "oauth_clients"("id") on delete cascade);
--INSERT INTO `oauth_sessions` VALUES(1,'client','testclient','testclient','');
--INSERT INTO `oauth_sessions` VALUES(2,'user',1,'testclient','');
--INSERT INTO `oauth_sessions` VALUES(3,'user',2,'testclient','');
CREATE TABLE "oauth_session_scopes" ("id" integer not null primary key autoincrement, "session_id" integer not null, "scope" varchar not null, foreign key("session_id") references "oauth_sessions"("id") on delete cascade, foreign key("scope") references "oauth_scopes"("id") on delete cascade);
CREATE TABLE "oauth_scopes" ("id" varchar not null, "description" varchar not null, primary key ("id"));
--INSERT INTO `oauth_scopes` VALUES('basic','Basic details about your account');
--INSERT INTO `oauth_scopes` VALUES('email','Your email address');
--INSERT INTO `oauth_scopes` VALUES('photo','Your photo');
CREATE TABLE "oauth_refresh_tokens" ("refresh_token" varchar not null, "expire_time" integer not null, "access_token" varchar not null, foreign key("access_token") references "oauth_access_tokens"("access_token") on delete cascade, primary key ("refresh_token"));
CREATE TABLE "oauth_clients" ("id" varchar not null, "secret" varchar not null, "name" varchar not null, primary key ("id"));
--INSERT INTO `oauth_clients` VALUES('testclient','secret','Test Client');
CREATE TABLE "oauth_client_redirect_uris" ("id" integer not null primary key autoincrement, "client_id" varchar not null, "redirect_uri" varchar not null);
--INSERT INTO `oauth_client_redirect_uris` VALUES(1,'testclient','http://example.com/redirect');
CREATE TABLE "oauth_auth_codes" ("auth_code" varchar not null, "session_id" integer not null, "expire_time" integer not null, "client_redirect_uri" varchar not null, foreign key("session_id") references "oauth_sessions"("id") on delete cascade, primary key ("auth_code"));
CREATE TABLE "oauth_auth_code_scopes" ("id" integer not null primary key autoincrement, "auth_code" varchar not null, "scope" varchar not null, foreign key("auth_code") references "oauth_auth_codes"("auth_code") on delete cascade, foreign key("scope") references "oauth_scopes"("id") on delete cascade);
CREATE TABLE "oauth_access_tokens" ("access_token" varchar not null, "session_id" integer not null, "expire_time" integer not null, foreign key("session_id") references "oauth_sessions"("id") on delete cascade, primary key ("access_token"));
--INSERT INTO `oauth_access_tokens` VALUES('iamgod',1,1458207696);
--INSERT INTO `oauth_access_tokens` VALUES('iamalex',2,1458207696);
--INSERT INTO `oauth_access_tokens` VALUES('iamphil',3,1458207696);
CREATE TABLE "oauth_access_token_scopes" ("id" integer not null primary key autoincrement, "access_token" varchar not null, "scope" varchar not null, foreign key("access_token") references "oauth_access_tokens"("access_token") on delete cascade, foreign key("scope") references "oauth_scopes"("id") on delete cascade);
